code.cpp:
int toggleIthBit(int n, int i) {
    if ((n & (1 << i)) != 0) {
        // Bit is set, clear it
        return n & ~(1 << i);
    } else {
        // Bit is clear, set it
        return n | (1 << i);
    }
}

