class Solution {
public:
    int missingNumber(vector<int>& nums) {
        int n = nums.size();

        // Check each number from 0 to n
        for (int num = 0; num <= n; num++) {
            bool found = false;

            // Search for num in array
            for (int i = 0; i < n; i++) {
                if (nums[i] == num) {
                    found = true;
                    break;
                }
            }

            // If not found, it's the missing number
            if (!found) {
                return num;
            }
        }

        return -1; // Should never reach here
    }
};

