code.cpp:
class Solution {
public:
    int missingNumber(vector<int>& nums) {
        int n = nums.size();

        // Calculate expected sum: 0+1+2+...+n = n*(n+1)/2
        int expectedSum = n * (n + 1) / 2;

        // Calculate actual sum
        int actualSum = 0;
        for (int num : nums) {
            actualSum += num;
        }

        // Missing number is the difference
        return expectedSum - actualSum;
    }
};

