bool lemonadeChange(vector<int>& bills) {
    int five = 0, ten = 0;

    for (int bill : bills) {
        if (bill == 5) {
            five++;
        } 
        else if (bill == 10) {
            if (five == 0) return false;
            five--;
            ten++;
        } 
        else { // bill == 20
            // Need $15 change
            // Prefer 1×$10 + 1×$5 over 3×$5
            if (ten > 0 && five > 0) {
                ten--;
                five--;
            } 
            else if (five >= 3) {
                five -= 3;
            } 
            else {
                return false;
            }
        }
    }

    return true;
}

// Example: bills = [5,5,5,10,20]
// bill=5: five=1, ten=0
// bill=5: five=2, ten=0
// bill=5: five=3, ten=0
// bill=10: five=2, ten=1 (give $5 change)
// bill=20: five=1, ten=0 (give $10+$5 change)
// Return true

// Example: bills = [5,5,10,10,20]
// bill=5: five=1, ten=0
// bill=5: five=2, ten=0
// bill=10: five=1, ten=1
// bill=10: five=0, ten=2
// bill=20: Need $15, have 0×$5, 2×$10
//          Can't give 1×$10+1×$5 (no $5)
//          Can't give 3×$5 (no $5)
// Return false

