code.cpp:
bool lemonadeChange(vector<int>& bills) {
    int five = 0, ten = 0;

    for (int bill : bills) {
        int change = bill - 5;

        if (change == 0) {
            five++;
        } 
        else if (change == 5) {
            if (five == 0) return false;
            five--;
            ten++;
        } 
        else if (change == 15) {
            // Try to make $15 change
            int tens_needed = min(change / 10, ten);
            change -= tens_needed * 10;
            ten -= tens_needed;

            int fives_needed = change / 5;
            if (five < fives_needed) return false;
            five -= fives_needed;
        }
    }

    return true;
}

