code.javascript:
var lemonadeChange = function(bills) {
    let five = 0, ten = 0;

    for (const bill of bills) {
        let change = bill - 5;

        if (change === 0) {
            five++;
        } else if (change === 5) {
            if (five === 0) return false;
            five--;
            ten++;
        } else if (change === 15) {
            const tensNeeded = Math.min(Math.floor(change / 10), ten);
            change -= tensNeeded * 10;
            ten -= tensNeeded;

            const fivesNeeded = Math.floor(change / 5);
            if (five < fivesNeeded) return false;
            five -= fivesNeeded;
        }
    }

    return true;
};
