string removeKdigits(string num, int k) {
    int n = num.length();
    if (k >= n) return "0";

    string stack;

    for (char digit : num) {
        while (!stack.empty() && stack.back() > digit && k > 0) {
            stack.pop_back();
            k--;
        }
        stack.push_back(digit);
    }

    // Remove remaining k digits from end
    while (k > 0) {
        stack.pop_back();
        k--;
    }

    // Remove leading zeros
    int start = 0;
    while (start < stack.size() && stack[start] == '0') {
        start++;
    }

    string result = stack.substr(start);
    return result.empty() ? "0" : result;
}

// Example: num = "1432219", k = 3
// stack = "1"
// digit='4': 4>1, push → stack="14"
// digit='3': 4>3, pop, k=2 → stack="13"
// digit='2': 3>2, pop, k=1 → stack="12"
// digit='2': 2=2, push → stack="122"
// digit='1': 2>1, pop, k=0 → stack="121"
// digit='9': push → stack="1219"
// Result: "1219"

code.java:
public String removeKdigits(String num, int k) {
    int n = num.length();
    if (k >= n) return "0";

    StringBuilder stack = new StringBuilder();

    for (char digit : num.toCharArray()) {
        while (stack.length() > 0 && stack.charAt(stack.length() - 1) > digit && k > 0) {
            stack.deleteCharAt(stack.length() - 1);
            k--;
        }
        stack.append(digit);
    }

    while (k > 0) {
        stack.deleteCharAt(stack.length() - 1);
        k--;
    }

    // Remove leading zeros
    while (stack.length() > 0 && stack.charAt(0) == '0') {
        stack.deleteCharAt(0);
    }

    return stack.length() == 0 ? "0" : stack.toString();
}

code.python:
def removeKdigits(num: str, k: int) -> str:
    if k >= len(num):
        return "0"

    stack = []

    for digit in num:
        while stack and stack[-1] > digit and k > 0:
            stack.pop()
            k -= 1
        stack.append(digit)

    # Remove remaining k digits
    while k > 0:
        stack.pop()
        k -= 1

    # Build result and remove leading zeros
    result = ''.join(stack).lstrip('0')
    return result if result else "0"

code.javascript:
var removeKdigits = function(num, k) {
    const n = num.length;
    if (k >= n) return "0";

    const stack = [];

    for (const digit of num) {
        while (stack.length > 0 && stack[stack.length - 1] > digit && k > 0) {
            stack.pop();
            k--;
        }
        stack.push(digit);
    }

    while (k > 0) {
        stack.pop();
        k--;
    }

    // Remove leading zeros and build result
    let result = stack.join('').replace(/^0+/, '');
    return result || "0";
};

