string removeKdigits(string num, int k) {
    if (k >= num.length()) return "0";

    string result;

    for (char c : num) {
        while (!result.empty() && result.back() > c && k > 0) {
            result.pop_back();
            k--;
        }
        if (!result.empty() || c != '0') {
            result.push_back(c);
        }
    }

    while (k > 0 && !result.empty()) {
        result.pop_back();
        k--;
    }

    return result.empty() ? "0" : result;
}

