code.python:
def removeKdigits(num: str, k: int) -> str:
    if k >= len(num):
        return "0"

    result = []

    for c in num:
        while result and result[-1] > c and k > 0:
            result.pop()
            k -= 1
        if result or c != '0':
            result.append(c)

    while k > 0 and result:
        result.pop()
        k -= 1

    return ''.join(result) if result else "0"

