var removeKdigits = function(num, k) {
    if (k >= num.length) return "0";

    const result = [];

    for (const c of num) {
        while (result.length > 0 && result[result.length - 1] > c && k > 0) {
            result.pop();
            k--;
        }
        if (result.length > 0 || c !== '0') {
            result.push(c);
        }
    }

    while (k > 0 && result.length > 0) {
        result.pop();
        k--;
    }

    return result.length === 0 ? "0" : result.join('');
};
