class Solution {
public:
    int firstUniqChar(string s) {
        int n = s.length();

        for (int i = 0; i < n; i++) {
            int count = 0;

            // Count occurrences of s[i] in entire string
            for (int j = 0; j < n; j++) {
                if (s[j] == s[i]) {
                    count++;
                }
            }

            // If character appears only once, return its index
            if (count == 1) {
                return i;
            }
        }

        return -1;
    }
};

