code.cpp:
int firstUniqChar(string s) {
    int n = s.length();

    for (int i = 0; i < n; i++) {
        int count = 0;
        for (int j = 0; j < n; j++) {
            if (s[j] == s[i]) {
                count++;
            }
        }
        if (count == 1) {
            return i;
        }
    }

    return -1;
}

