public int firstUniqChar(String s) {
    int[] count = new int[26];

    for (char c : s.toCharArray()) {
        count[c - 'a']++;
    }

    for (int i = 0; i < s.length(); i++) {
        if (count[s.charAt(i) - 'a'] == 1) {
            return i;
        }
    }

    return -1;
}

code.python:
def firstUniqChar(s):
    count = [0] * 26

    for c in s:
        count[ord(c) - ord('a')] += 1

    for i, c in enumerate(s):
        if count[ord(c) - ord('a')] == 1:
            return i

    return -1

