int pivotIndex(vector<int>& nums) {
    int n = nums.size();

    for (int i = 0; i < n; i++) {
        int leftSum = 0;
        for (int j = 0; j < i; j++) {
            leftSum += nums[j];
        }

        int rightSum = 0;
        for (int j = i + 1; j < n; j++) {
            rightSum += nums[j];
        }

        if (leftSum == rightSum) {
            return i;
        }
    }

    return -1;
}

