code.cpp:
int pivotIndex(vector<int>& nums) {
    int n = nums.size();
    vector<int> prefix(n + 1, 0);
    vector<int> suffix(n + 1, 0);

    // Build prefix sum
    for (int i = 0; i < n; i++) {
        prefix[i + 1] = prefix[i] + nums[i];
    }

    // Build suffix sum
    for (int i = n - 1; i >= 0; i--) {
        suffix[i] = suffix[i + 1] + nums[i];
    }

    // Find pivot
    for (int i = 0; i < n; i++) {
        if (prefix[i] == suffix[i + 1]) {
            return i;
        }
    }

    return -1;
}

