function pivotIndex(nums) {
    const total = nums.reduce((sum, num) => sum + num, 0);

    let leftSum = 0;
    for (let i = 0; i < nums.length; i++) {
        if (leftSum === total - leftSum - nums[i]) {
            return i;
        }
        leftSum += nums[i];
    }

    return -1;
