class Solution {
public:
    int maxSumSubarray(vector<int>& arr, int k) {
        int n = arr.size();
        if (n < k) return -1;

        // Calculate sum of first window
        int windowSum = 0;
        for (int i = 0; i < k; i++) {
            windowSum += arr[i];
        }

        int maxSum = windowSum;

        // Slide the window
        for (int i = k; i < n; i++) {
            windowSum = windowSum - arr[i - k] + arr[i];
            maxSum = max(maxSum, windowSum);
        }

        return maxSum;
    }
};

