class Solution:
    def maxSumSubarray(self, arr: List[int], k: int) -> int:
        n = len(arr)
        if n < k:
            return -1

        # Calculate sum of first window
        window_sum = sum(arr[:k])
        max_sum = window_sum

        # Slide the window
        for i in range(k, n):
            window_sum = window_sum - arr[i - k] + arr[i]
            max_sum = max(max_sum, window_sum)

        return max_sum

