class Solution {
public:
    int maxSumSubarray(vector<int>& arr, int k) {
        int n = arr.size();
        if (n < k) return -1;

        int maxSum = INT_MIN;

        for (int i = 0; i <= n - k; i++) {
            int currentSum = 0;
            for (int j = i; j < i + k; j++) {
                currentSum += arr[j];
            }
            maxSum = max(maxSum, currentSum);
        }

        return maxSum;
    }
};

