class Solution:
    def maxSumSubarray(self, arr: List[int], k: int) -> int:
        n = len(arr)
        if n < k:
            return -1

        max_sum = float('-inf')

        for i in range(n - k + 1):
            current_sum = sum(arr[i:i+k])
            max_sum = max(max_sum, current_sum)

        return max_sum

