code.cpp:
class Solution {
public:
    void reverseString(vector<char>& s) {
        stack<char> st;

        // Push all characters
        for (char c : s) {
            st.push(c);
        }

        // Pop and assign back
        for (int i = 0; i < s.size(); i++) {
            s[i] = st.top();
            st.pop();
        }
    }
};

code.java:
class Solution {
    public void reverseString(char[] s) {
        Stack<Character> stack = new Stack<>();

        // Push all characters
        for (char c : s) {
            stack.push(c);
        }

        // Pop and assign back
        for (int i = 0; i < s.length; i++) {
            s[i] = stack.pop();
        }
    }
}

