public void sortColors(int[] nums) {
    int low = 0, mid = 0, high = nums.length - 1;

    while (mid <= high) {
        if (nums[mid] == 0) {
            // Swap 0 to the left section
            int temp = nums[low];
            nums[low] = nums[mid];
            nums[mid] = temp;
            low++;
            mid++;
        } else if (nums[mid] == 1) {
            // 1 is already in correct position
            mid++;
        } else {
            // Swap 2 to the right section
            int temp = nums[mid];
            nums[mid] = nums[high];
            nums[high] = temp;
            high--;
            // Don't increment mid as we need to check swapped element
        }
    }
}

