class Solution:
    def removeElement(self, nums: List[int], val: int) -> int:
        temp = []

        for num in nums:
            if num != val:
                temp.append(num)

        for i in range(len(temp)):
            nums[i] = temp[i]

        return len(temp)

code.javascript:
var removeElement = function(nums, val) {
    const temp = [];

    for (let i = 0; i < nums.length; i++) {
        if (nums[i] !== val) {
            temp.push(nums[i]);
        }
    }

    for (let i = 0; i < temp.length; i++) {
        nums[i] = temp[i];
    }

    return temp.length;
};

